INSERT INTO librarysystem.Authors (ol_author_key, author_name, biography, author_image_url) VALUES
    ('OL498120A', 'Harper Lee', 'Nelle Harper Lee was an American novelist. She wrote the 1960 novel To Kill a Mockingbird that won the 1961 Pulitzer Prize and became a classic of modern American literature. Lee received numerous accolades and honorary degrees, including the Presidential Medal of Freedom in 2007 which was awarded for her contribution to literature. She assisted her close friend Truman Capote in his research for the book In Cold Blood (1966). Capote was the basis for the character Dill Harris in To Kill a Mockingbird.', 'book_data/authors/OL498120A.jpg');
INSERT INTO librarysystem.Authors (ol_author_key, author_name, biography, author_image_url) VALUES
    ('OL19441A', 'Joseph Conrad', 'Joseph Conrad was a Polish-born British novelist, who became a British subject in 1886. He is regarded as one of the greatest novelists in English though he did not speak the language fluently until he was in his twenties (and then always with a marked Polish accent). He wrote stories and novels, predominantly with a nautical or seaboard setting, that depict trials of the human spirit by the demands of duty and honor. ([Source][1].)

[1]:https://en.wikipedia.org/wiki/Joseph_Conrad', 'book_data/authors/OL19441A.jpg');
