INSERT INTO librarysystem.Authors (ol_author_key, author_name, biography, author_image_url) VALUES
    ('OL70857A', 'T. E. Lawrence', 'Lieutenant Colonel Thomas Edward Lawrence, CB, DSO (16 August 1888 – 19 May 1935), known professionally as T. E. Lawrence, was a British Army officer renowned especially for his liaison role during the Arab Revolt against Ottoman Turkish rule of 1916–18. The extraordinary breadth and variety of his activities and associations, and his ability to describe them vividly in writing, earned him international fame as Lawrence of Arabia, a title popularised by the 1962 film based on his First World War activities. [Wikipedia]', 'book_data/authors/OL70857A.jpg');
